import{o as d,c as u,p as h,a as f,b as e,r as _,t as v,d as b,e as z,n as S,f as t,w as c,g as p,h as q}from"./vendor.4da7eef0.js";const I=function(){const a=document.createElement("link").relList;if(a&&a.supports&&a.supports("modulepreload"))return;for(const o of document.querySelectorAll('link[rel="modulepreload"]'))s(o);new MutationObserver(o=>{for(const n of o)if(n.type==="childList")for(const m of n.addedNodes)m.tagName==="LINK"&&m.rel==="modulepreload"&&s(m)}).observe(document,{childList:!0,subtree:!0});function r(o){const n={};return o.integrity&&(n.integrity=o.integrity),o.referrerpolicy&&(n.referrerPolicy=o.referrerpolicy),o.crossorigin==="use-credentials"?n.credentials="include":o.crossorigin==="anonymous"?n.credentials="omit":n.credentials="same-origin",n}function s(o){if(o.ep)return;o.ep=!0;const n=r(o);fetch(o.href,n)}};I();var P="/video/IlPalazzodeiDestini.mp4";var g=(i,a)=>{const r=i.__vccOpts||i;for(const[s,o]of a)r[s]=o;return r};const y={},$=i=>(h("data-v-ee83cd16"),i=i(),f(),i),L={id:"video",class:"bg-black d-flex justify-content-center"},G=$(()=>e("video",{controls:"",autoplay:""},[e("source",{src:P,type:"video/mp4"})],-1)),C=[G];function D(i,a,r,s,o,n){return d(),u("section",L,C)}var N=g(y,[["render",D],["__scopeId","data-v-ee83cd16"]]);const O={},A={id:"title",class:"p-3 d-flex flex-column align-items-center mb-3"},M=e("span",{class:"display-4"},"La Sinfonia dei Sette ",-1),T=e("span",{class:"display-2"},"Il Palazzo dei Destini Incrociati",-1),V=e("span",{class:"display-6"},"23/24 Aprile 2022 ",-1),R=e("span",{class:"display-6"},"Villa Achillini, Sasso Marconi (Bo)",-1),x=[M,T,V,R];function U(i,a,r,s,o,n){return d(),u("div",A,x)}var F=g(O,[["render",U]]);const k=i=>(h("data-v-22570868"),i=i(),f(),i),w={class:"row justify-content-center card-bg"},E={class:"col-12 text-center"},B={class:"display-3"},Q=k(()=>e("div",{class:"col-8 border-bottom border-1 border-dark my-3"},null,-1)),j={class:"col-7 text-center"},H=["innerHTML"],K={props:{title:{type:String,required:!0},incipit:{type:String,default:""},align:{type:String,default:"center"}},setup(i){const a=_(!1);return(r,s)=>(d(),u("div",w,[e("div",E,[e("span",B,v(i.title),1)]),Q,e("div",j,[i.incipit.length>0?(d(),u("p",{key:0,class:"fst-italic",innerHTML:i.incipit},null,8,H)):b("",!0),e("button",{class:"btn border border-1 mb-3",onClick:s[0]||(s[0]=o=>a.value=!a.value)},v(a.value?"Chiudi":"Espandi"),1)]),e("div",{class:S(["col-6 overflow-hidden expandable",a.value?`text-${i.align}`:`text-${i.align} h-0`])},[z(r.$slots,"default",{},void 0,!0)],2)]))}};var l=g(K,[["__scopeId","data-v-22570868"]]);const J={class:"container-fluid p-0"},W={id:"video"},Y={id:"welcome",class:"mb-4"},X={id:"sinfonia",class:"mb-4"},Z=e("p",null,"Sette furono gli Dei. Sette cantarono e cos\xEC cantando crearono il Tutto.",-1),ee=e("p",null," La prima Hava cantando si cav\xF2 dalle viscere il cielo e lo pose sopra alla terra, poi per sua volont\xE0, terra e cielo si inclinarono cos\xEC da potersi incontrare. ",-1),ie=e("p",null," Opera del secondo, Samudra, furono i cicli dei mari, della terra e delle moltitudine delle acque. Lei costru\xEC la grande scala da cui scende la pioggia. ",-1),oe=e("p",null," Il terzo Yud\u2019ah cantando, si assicur\xF2 che tutti avrebbero lavorato nel grande firmamento, affond\xF2 le mani e i piedi nella terra e form\xF2 i metalli del sottosuolo. ",-1),ae=e("p",null," Samajhadara, il quarto, mise la terra dove non vi era niente, poi soffi\xF2 il vento e poich\xE9 era sapiente, lo chiam\xF2 spirito, perch\xE9 in lui non vi era morte. ",-1),ne=e("p",null," Il quinto Andhera cre\xF2 la notte e la morte, cre\xF2 il mondo degli inferi e li destin\xF2 a chi aveva veleno nell'anima. ",-1),te=e("p",null," Opera di Mausama, il sesto, fu la crescita delle piante e degli animali, mut\xF2 il tempo atmosferico e mosse la ruota nel tempo. ",-1),re=e("p",null,[p(" Dall'ultimo, chiamato Surya, usc\xEC la prima luce, e la mantenne accesa in una sfera ardente che pose nel cielo. "),e("br"),p(" Poi con la terra impast\xF2 il fango e ne usc\xEC una forma umanoide, poi prese dalla sfera ardente un po della luce che le aveva posto dentro e la pos\xF2 su queste forme inanimate, cos\xEC facendo nacque l'anima primordiale che si irradi\xF2 attraverso il corpo sprigionando l'aurea, e cos\xEC gli don\xF2 la vita. ")],-1),se={id:"ambientazione",class:"mb-4"},le=e("p",null,[p(" La nostra ambientazione di divide in cinque macro regioni, ognuna con caratteristiche e peculiarit\xE0 proprie. "),e("br"),p(" Non preoccuparti se non conosci a menadito l\u2019ambientazione di Fulcron: ad ogni PGOS verranno date le informazioni necessarie per giocare senza problemi e, se vorrai approfondire, potrai sempre consultare la nostra Wikia, disponibile sul nostro sito. ")],-1),ce=e("p",null," Il Nord, ispirato all\u2019Europa del basso medioevo, \xE8 composto da una grande variet\xE0 di Regni. Tra i pi\xF9 significativi ricordiamo il Regno di Ilshaka, retto dal grande sovrano guerriero Nymbra Shoot, un re ormai molto anziano che fu decisivo nel mantenere il Nord unito in molte delle guerre del passato, e Lungacosta, regno affacciato sul mare governato da una regina ambiziosa, Elaine Moonsharp. ",-1),de=e("p",null," L\u2019Est, ispirato alle grandi civilt\xE0 asiatiche e nordafricane, l\u2019Est \xE8 suddiviso in tre grandi regioni. Verso nord troviamo le Citt\xE0 Libere del Deserto, che si governano in autonomia, Tra di esse spicca Partaros, da cui si \xE8 originato il grande e potente Ordine dei Guaritori. Nelle Terre Verdi di Jotai sorge invece l\u2019Impero dei Dodici Fiori, attualmente governato dall\u2019autoproclamatosi imperatore Doraan. Infine, il sultanato degli Arak, composto da nomadi del deserto governati da un ricco e potente sultano. ",-1),ue=e("p",null," Il Sud, ispirato alla cultura greca e romana del mondo classico. Una grande muraglia si estendeva lungo tutto il confine meridionale del regno e lo separava dai Diaforos, creature orribili e bellicose. Qualche anno fa il muro \xE8 crollato, stravolgendo ogni equilibrio geopolitico della zona. Attualmente il Sud \xE8 frammentato e diviso: l\u2019Antico Regno cerca di resistere, mentre una nuova entit\xE0 territoriale, Nea Imera, \xE8 sorta con la speranza di unire uomini e Diaforos. Intere regioni sono perdute nell\u2019anarchia e nel disordine mentre nelle Tre Gemme del Sud, ricche citt\xE0 di traffici e commerci, i mercanti hanno approfittato della situazione per prendere il potere. ",-1),pe=e("p",null," L\u2019Ovest, ispirato alle americhe precolombiane. Grandi foreste vergini si estendono per tutto il territorio e le grandi potenze di Fulcron competono per colonizzare la zona. Grandi trib\xF9 di guerrieri nomadi vivono in queste terre. ",-1),me=e("p",null," Infine, il Mare Interno, il grande oceano che si trova al centro di Fulcron e che gli conferisce la caratteristica forma ad Anello. Molti sono i pirati che solcano questi mari, dato che nessun regnante \xE8 mai stato abbastanza potente da estendere il suo controllo su di essi. Nove sono i pi\xF9 grandi pirati che si autoproclamano Nobili, guidati, se cos\xEC si pu\xF2 dire, dal Re Bambino, mentre nel Mare di Ossa, nascosta e sempre in agguato Mim\xEC Lorane, la piratessa in esilio attende il momento esatto in cui poter vendicarsi e diventare Regina dei Mari. ",-1),ge={id:"palazzo",class:"mb-4"},ve=e("p",null," Muthalath, Komiteia, e ancora Owari. Molti sono i nomi dati nel tempo a questa terra. Cos\xEC vicina a tutti da essere una facile conquista, ma cos\xEC poco interessante da sfuggire alle brame di chiunque. Snodandosi nei stretti sentieri all\u2019interno delle montagne, questa terra mette in comunicazione le ricche Gemme del Sud, l\u2019orgoglioso Impero dei Dodici Fiori e l\u2019esotico Sultanato. Eppure il passaggio \xE8 impervio, labirintico e la leggenda vuole che nessuno vi faccia ritorno, per questo \xE8 divenuto noto a molti come il Passo dei Perduti. Una villa ricca e sfarzosa si dice sia sorta all\u2019interno di quel labirinto, l\xEC all\u2019incrocio della vie dove ogni viandante \xE8 guidato dal fato: il Palazzo dei Destini Incrociati. ",-1),he=e("p",null," L\u2019autunno \xE8 sceso sull\u2019Impero dei Dodici Fiori, i petali lasciano i loro steli in una guerra civile che sta mettendo i nobili gli uni contro gli altri. Da una parte i sostenitori della casata imperiale, prossima all\u2019estinzione dopo la morte dell\u2019imperatore, e dall\u2019altra i seguaci del nuovo imperatore, lo straniero Doraan di Partaros, detto Lo Scorpione. ",-1),fe=e("p",null," Il vento caldo del Grande Deserto porta dalle terre dell\u2019est solo qualche flebile sussurro. Ben poco si conosce del misterioso Sultanato, retto dagli dei-sovrani della casata Arak. Eppure in molti hanno udito in quelle deboli voci terrore e insicurezza. ",-1),_e=e("p",null," Dal Sud giunge invece un continuo flusso di merci pregiate. Tre grandi, ricche e sfarzose citt\xE0 sorgono infatti poco lontano da Komiteia: le Tre Gemme del Sud. Un tempo poste sotto il dominio dell\u2019Antico Regno, sono ora governate da mercanti spregiudicati e ambiziosi. Terre in cui il lusso e il piacere sono diventate nuove divinit\xE0. ",-1),be={id:"grv",class:"mb-4"},ze=e("p",null," Immagina di essere il protagonista di un film senza copione n\xE9 pubblico, in cui l\u2019attore \xE8 anche regista. Il set \xE8 l\u2019intera location di gioco che hai a disposizione, ma non ci sar\xE0 nessuno a giudicarti o ad applaudirti. Improvvisando liberamente in modo spontaneo e naturale, sarai TU il protagonista della tua storia, insieme agli altri giocatori co-attori che interpreteranno con te il dramma ed il riso, il conflitto o l\u2019unione. ",-1),Se=e("p",null," Immagina di essere l\u2019autore di un romanzo ancora da scrivere, e di avere carta bianca sul finale, i legami, gli intrecci fra i personaggi. Di poter raccontare, inventare, creare liberamente. Dove sei TU il co-autore della storia ed ogni tua azione, sentimento, decisione influenza il corso della narrazione, insieme agli altri personaggi. ",-1),qe=e("p",null," Immagina di vivere una vita parallela in un mondo di finzione ricreato ad hoc tramite un\u2019atmosfera immersiva grazie all\u2019utilizzo di scenografie e costumi appositi. Un\u2019illusione dentro la realt\xE0, un\u2019opportunit\xE0 per cambiare punto di vista in un universo non cos\xEC distante. Immagina di vivere in prima persona una storia unica, nata e creata solo nel momento in cui \xE8 vissuta dai partecipanti stessi. Un\u2019esperienza di condivisione, conflitto, azione e riflessione comune. ",-1),Ie=e("p",null," Un sipario alternativo aperto solo nell\u2019istante in cui viene giocato, ma che continua a sopravvivere nei ricordi, nei legami e nelle emozioni provate dai giocatori che lo hanno vissuto. ",-1),Pe=e("p",null,"Questo e molto di pi\xF9 \xE8 il LARP.",-1),ye={id:"new-game",class:"mb-4"},$e=e("p",null," In una Campagna si interpretano gli stessi personaggi per un lungo periodo di tempo. In questo modo, si vengono a creare profondi legami tra di essi, si formano gruppi e si lotta per obiettivi comuni. Pu\xF2 essere paragonata a una Serie Tv, in cui i protagonisti compaiono puntata dopo puntata. In una OneShot, invece, l\u2019evento inizia e finisce nel giro di qualche giorno. I personaggi interpretati vengono abbandonati, e spesso le situazioni che si vengono a creare sono pi\xF9 cariche di pathos e drammaticit\xE0. Sono paragonabili a Film: i protagonisti sono sempre diversi, cos\xEC come le storie che vengono raccontate. La nostra idea \xE8 quella di unire queste due tipologie in qualcosa di nuovo, in modo da godere dei vantaggi di entrambe. ",-1),Le=e("p",null," C\u2019\xE8 una storia generale, un disegno di insieme, ma ogni evento pulsa di vita propria e sa donare a qualsiasi dei giocatori che vi hanno partecipato grandi emozioni. Per fare questo, abbiamo pensato di dividere i personaggi in due categorie: i Personaggi Campagna (PG) e i Personaggi One Shot (PGOS). ",-1),Ge=e("p",null," I PG vengono creati da voi giocatori. Potrete decidere se interpretare un pavido mercante, un tenace guerriero, una nobile dama, la scelta \xE8 vostra. Potrete decidere di interpretare il PG da voi creato ad ogni evento della Sinfonia dei Sette e per voi l\u2019esperienza sar\xE0 quindi simile a quella di una Campagna: il vostro personaggio crescer\xE0 nel tempo, diventando pi\xF9 esperto e pi\xF9 forte; stringer\xE0 legami profondi con altri personaggi e, nel tempo, potrete dare forma alla sua storia. Per creare un PG \xE8 fondamentale scrivere un background che non deve essere n\xE9 troppo breve ma neanche troppo articolato, ma deve descrivere al meglio la storia del vostro personaggio. Una volta creato \xE8 necessario inviarlo al gruppo ambientazione che lo valuter\xE0 e, se presenti, ti chieder\xE0 di correggere eventuali errori o incongruenze con la trama gi\xE0 presente. ",-1),Ce=e("p",null," I PGOS vengono forniti in numero limitato dallo Staff, e saranno diversi per ogni evento. Sono del tutto simili ai personaggi che si possono trovare nelle OneShot: ognuno di essi sar\xE0 ben caratterizzato, avr\xE0 segreti, relazioni e obiettivi. Una volta che avrete scelto quale tra i PGOS disponibili sia pi\xF9 adatto per voi, tutto ci\xF2 che vi serve per giocare sar\xE0 indicato nella Scheda del Personaggio che vi verr\xE0 fornita dallo Staff. Sono i personaggi ideali per chi non pu\xF2 essere presente con continuit\xE0 a tutti gli eventi della Sinfonia dei Sette, per i nuovi giocatori o per tutti coloro che amano calarsi ogni volta in una parte diversa. ",-1),De=e("p",null," Negli eventi di campagna della \u201Csinfonia dei sette\u201D di Fulcron GRV, \xE8 possibile scegliere se partecipare come Personaggio Giocante (PG) o come Personaggio Giocante One Shot (PGOS). Quest\u2019ultimo, una volta completata l\u2019iscrizione potrai scegliere uno fra i tanti protagonisti di questo evento selezionando uno dei Teaser. Il Teaser identifica le caratteristiche chiave ed i concetti fondamentali che rendono unico quel personaggio. In vicinanza dell\u2019evento ti verr\xE0 consegnata tramite mail anche la Scheda Personaggio completa di numerosi spunti di gioco, come segreti, relazioni, intrighi e molto altro. Si tratta di una \u201Ccarta d\u2019identit\xE0\u201D di partenza che ha lo scopo di darti spunti interessanti per giocare e caratterizzare al meglio il tuo personaggio, non un copione da seguire. Ricordati che sarai tu a raccontare la storia del tuo personaggio...insieme agli altri partecipanti! ",-1),Ne={id:"golden-rule"},Oe=e("ul",null,[e("li",{class:"list-unstyled mb-2 border-bottom border-1"}," Rimani sempre nel personaggio: con le parole, con le azioni e coi pensieri. Non uscire mai dal gioco, non parlare d\u2019altro: il gioco \xE8 bello ed emozionante se tutti lo rendono vero e vivo a beneficio degli altri partecipanti. "),e("li",{class:"list-unstyled mb-2 border-bottom border-1"}," A ogni azione, una reazione: se un altro personaggio ti rivela un segreto, ti insulta, ti nomina suo vice, ti pugnala a tradimento... reagisci. Non importa che cosa fai o dici, ma fai qualcosa, possibilmente qualcosa di appropriato, di divertente, di drammatico. In un larp nessuna azione deve cadere nel vuoto. "),e("li",{class:"list-unstyled mb-2 border-bottom border-1"}," Accetta le azioni altrui: in un larp ciascuno \xE8 regista di se stesso. Non mettere mai in dubbio le azioni o la buona fede degli altri e non aspettarti mai da loro una reazione specifica. Prendi sempre per buono tutto quello che succede e gioca di conseguenza. In un larp non c\u2019\xE8 copione, non ci sono prove e non si rigirano le scene; non tutto sar\xE0 perfetto, ma tutto sar\xE0 convincente, se decidi di crederci. "),e("li",{class:"list-unstyled mb-2 border-bottom border-1"}," Quando si interpretano situazioni drammatiche insieme ad altri giocatori, \xE8 bene avere un semplice set di regole base per gestire al meglio il tuo coinvolgimento senza imbarazzo o fastidio. Vogliamo che tu sia in grado di comunicare chiaramente i tuoi limiti, e decidere l\u2019intensit\xE0 di gioco che preferisci senza interrompere il flusso della scena. Usa sempre il buon senso e non fare nulla di pericoloso: nessuno deve farsi male. "),e("li",{class:"list-unstyled mb-2 border-bottom border-1"}," Se durante il gioco succede qualcosa che trovi troppo sgradevole o invadente, puoi (e devi!) usare la frase di sicurezza \u201CVacci piano!\u201D: ci\xF2 indicher\xE0 all\u2019altro giocatore che deve evitare di essere troppo realistico, lasciarti spazio, abbassare la tensione, badare di non farti male. "),e("li",{class:"list-unstyled mb-2 border-bottom border-1"}," Non c\u2019\xE8 nulla di male ad usare il \u201CVacci piano\u201D e ricordati sempre di obbedire se qualcuno te lo dice: ciascuno ha la sua sensibilit\xE0 e il suo metro di giudizio e tu devi rispettarli ciecamente. "),e("li",{class:"list-unstyled mb-2 border-bottom border-1"}," Pi\xF9 spesso, pu\xF2 capitare che un giocatore sia troppo prudente o timido e, non volendo rischiare di infastidire l\u2019altra persona, interpreti una scena in modo fin troppo astratto. In questi casi puoi usare la frase di sicurezza \u201C\xC8 tutto qui?\u201D: indica all\u2019altro giocatore che, se vuole, pu\xF2 andarci un po\u2019 pi\xF9 pesante, aumentare l\u2019intensit\xE0 della scena. "),e("p",null," Inoltre se doveste notare che un giocatore vi si avvicina troppo, senza rispettare il distanziamento previsto dalle norme antiCOVID potete pronunciare la safeword \u201CMi manca l\u2019aria!\u201D accompagnandolo o meno con un gesto delle mani con i palmi protesi in avanti. L\u2019altro giocatore capir\xE0 che si sta avvicinando troppo e si allontaner\xE0 immediatamente. Se un giocatore dovesse dimenticarsi la safeword e compie comunque il gesto previsto, l\u2019altro giocatore deve ugualmente rispettare la distanza ed allontanarsi. ")],-1),Ae={id:"rules"},Me={id:"pgos"},Te=e("p",null," Tutti i personaggi di questo magnifico intreccio hanno un'identit\xE0 nascosta ben coperta da una colorata maschera. Infatti le maschere possono essere di cartapesta, altre dipinte sul volto, altre ancora le indossiamo, anche senza vederle fisicamente. Questo \xE8 quello che interpreterai nel palazzo, una maschera, ma attent\u0259 a ricordare chi eri sotto la maschera, perch\xE8 indossandola per molto tempo potresti non ricordartelo pi\xF9. ",-1),Ve=e("p",null," Post Scriptum Mi dicono che quell'uomo abbia pianto a lungo, quella notte, mentre la ragazza fuggiva. Lui rimase nudo sotto la maschera, lei ne trov\xF2 una nuova. O forse lei altro non aveva che il suo volto. Chi pu\xF2 dirlo? ",-1),Re={setup(i){return(a,r)=>(d(),u("main",J,[e("section",W,[t(N)]),e("section",Y,[t(F)]),e("section",X,[t(l,{title:"La Sinfonia dei Sette",incipit:`Cos\xEC nacque Fulcron, l\u2019Anello, e con esso le razze dei mortali che lo abitano. Ere dopo la sua creazione,
              tanti sono i regni che i mortali hanno creato.`},{default:c(()=>[Z,ee,ie,oe,ae,ne,te,re]),_:1})]),e("section",se,[t(l,{title:"La Nostra Ambientazione"},{default:c(()=>[le,ce,de,ue,pe,me]),_:1})]),e("section",ge,[t(l,{title:"Il Palazzo dei Destini Incrociati",incipit:`Scorre, il filo del fato. 
Veloce si dipana dalla matassa. 
Corre attraverso i segreti del Tempo, mentre il Caso ridendo lo accompagna. 
Insieme, formano quella cosa che noi mortali chiamiamo Destino. 
Se tu potessi vederlo, tu che posi gli occhi su queste righe, assisteresti a uno spettacolo unico: decine di fili cangianti che si uniscono in un unico punto. La Magione de Van Gaussi\xE9. 
Qualcuno ancora nemmeno immagina che questa sia la destinazione cui \xE8 destinato, mentre altri  gi\xE0 godono della ospitalit\xE0 del padrone di casa, Tremelech de Van Gaussi\xE9.
Ah, il magnifico ospite. 
 Molti hanno sentito parlare di lui, pochi lo hanno visto, nessuno pu\xF2 dire di conoscerlo. Sempre cos\xEC elegante, cos\xEC generoso nell'accogliere i viandanti. Un esteta. Un genio. Un folle?
Non lo sai. Come non sai chi sia a governare su queste terre di frontiera. Come non sai chi altro verr\xE0 condotto dal Destino questa sera qui, in questa villa, a questo banchetto.
Forse, \xE8 ora che prepari la tua storia, caro lettore. 
D'altronde, cosa siamo noi se non quello che diciamo di essere? Diventiamo ci\xF2 che gli altri vedono quando ci guardano, dopotutto. 
Forse, questa sera sar\xE0 pi\xF9 necessaria che mai una maschera. 
Indossala, caro lettore, e benvenuto.
`},{default:c(()=>[ve,he,fe,_e]),_:1})]),e("section",be,[t(l,{title:"Il Gioco di Ruolo dal Vivo",incipit:`Fulcron \xE8 il mondo fantastico in cui sono ambientate le nostre fantastiche storie, ma prima di addentrarti nei suoi territori inesplorati forse \xE8 meglio
parlare di cosa sia un Gioco di Ruolo dal Vivo, anche detto Larp. Per darvi un idea voglio fare un piccolo paragone che forse vi far\xE0 sorridere: tutti da bambini avete impugnato una spada o un bastone e immaginato di essere i soli eroi in grado di sconfiggere il malvagio di turno.
`},{default:c(()=>[ze,Se,qe,Ie,Pe]),_:1})]),e("section",ye,[t(l,{title:"Un Nuovo Modo di Giocare",incipit:"Nel mondo del Gioco di Ruolo dal Vivo esistono essenzialmente due grandi tipologie di evento: le Campagne Continuative e le OneShot\u2026"},{default:c(()=>[$e,Le,Ge,Ce,De]),_:1})]),e("section",Ne,[t(l,{title:"Le Rogle d'Oro del LARP",align:"start"},{default:c(()=>[Oe]),_:1})]),e("section",Ae,[t(l,{title:"Il Regolamento e Regole Speciali"})]),e("section",Me,[t(l,{title:"I PGOS del Palazzo dei Destini Incrociati",incipit:`Cos'\xE8, alla fine, una maschera? <br>
Cartapesta e vernice che, sul tuo volto, creano qualcosa di magnifico o di spaventoso?
Al ballo c'era un uomo con una maschera da lupo e una donna con la maschera da agnello.<br>
Non ti racconto il finale.<br>
Ma sotto alla maschera cosa troviamo?<br>
Pelle e parole che, sul tuo volto, creano qualcosa di magnifico o di spaventoso?
Al ballo c'era un uomo senza una maschera, con occhi da lupo, e una ragazza con occhi birbanti.<br>
Non ti racconto il finale.<br>
Cosa c'\xE8 sotto la cartapesta, sotto la pelle?<br>
Siamo altro oltre alle nostre menzogne?<br>
Sono domande cui non conosco risposta.<br>
Cercala tu, se lo desideri. Ma attenzione: quando provi a strappare una maschera, i lacci che tengono stretta la tua improvvisamente si fanno pi\xF9 lenti.<br> <br>

La lista sar\xE0 disponibile a breve
`},{default:c(()=>[Te,Ve]),_:1})])]))}};q(Re).mount("#app");
